import{products}from"../data/data.js";const showProducts=()=>{const t=document.getElementById("all-products");t.innerHTML="",products.forEach((e=>{const n=document.createElement("li");n.className="flex py-6 sm:py-10",n.innerHTML=`\n          <div class="flex-shrink-0">\n              <img src="${e.image}"\n                  alt="${e.name}"\n                  class="h-24 w-24 rounded-lg object-contain object-center sm:h-32 sm:w-32">\n          </div>\n\n          <div class="relative ml-4 flex flex-1 flex-col justify-between sm:ml-6">\n              <div>\n                  <div class="flex justify-between sm:grid sm:grid-cols-2">\n                      <div class="pr-6">\n                          <h3 class="text-md">${e.name}</h3>\n                          <h2 class="text-sm">${e.label}</h2>\n                          <p class="mt-1 text-sm text-gray-500">${e.description}</p>\n                          <p class="mt-1 text-sm text-gray-500">${e.model}</p>\n                      </div>\n\n                      <p class="text-right text-sm font-medium text-gray-900">$${e.price}</p>\n                  </div>\n\n                  <div class="flex flex-col items-start sm:absolute sm:left-1/2 sm:top-0 sm:mt-0">\n                      <button\n                          onclick="window.location.href = './edit-product.html'"\n                          type="button"\n                          class="text-sm font-medium text-primary-600 hover:text-indigo-500 mt-4 sm:mt-0"\n                      >\n                          Edit product\n                      </button>\n                      <button\n                          type="button"\n                          class="text-sm font-medium text-red-600 hover:text-red-500 mt-2"\n                      >\n                          Delete product\n                      </button>\n                  </div>\n              </div>\n\n              <p class="mt-4 flex space-x-2 text-sm text-gray-700">\n                  ${e.count} in stock\n              </p>\n          </div>\n    `,t.appendChild(n)}))};document.addEventListener("DOMContentLoaded",(()=>{showProducts()}));
