import{collections}from"../data/collections.js";const showProducts=()=>{const t=document.getElementById("all-collections");t.innerHTML="",collections.forEach((n=>{const e=document.createElement("li");e.className="flex py-6 sm:py-10",e.innerHTML=`\n      <div class="flex flex-col justify-between sm:grid sm:grid-cols-4">\n          <div class="col-span-3">\n              <h3 class="text-md">${n.name}</h3>\n              <h2 class="text-sm">${n.label}</h2>\n              <p class="mt-1 text-sm text-gray-500">${n.description}</p>\n          </div>\n          <div class="flex flex-col items-start sm:ml-auto">\n               <button\n                   onclick="window.location.href = './edit-collection.html'"\n                   type="button"\n                   class="text-sm font-medium text-primary-600 hover:text-indigo-500 mt-4 sm:mt-0"\n               >\n                   Edit collection\n               </button>\n               <button\n                   type="button"\n                   class="text-sm font-medium text-red-600 hover:text-red-500 mt-2"\n               >\n                   Delete collection\n               </button>\n          </div>\n      </div>\n    `,t.appendChild(e)}))};document.addEventListener("DOMContentLoaded",(()=>{showProducts()}));
