import Carousel from"./carousel.min.js";import{products}from"../data/data.js";const renderCarousel=()=>{const e=document.getElementById("carousel-example"),t=[{position:0,el:document.getElementById("carousel-item-1")},{position:1,el:document.getElementById("carousel-item-2")}],n={defaultPosition:1,interval:3e3,indicators:{activeClasses:"bg-white dark:bg-gray-800",inactiveClasses:"bg-white/50 dark:bg-gray-800/50 hover:bg-white dark:hover:bg-gray-800",items:[{position:0,el:document.getElementById("carousel-indicator-1")},{position:1,el:document.getElementById("carousel-indicator-2")}]}},o=new Carousel(e,t,n,{id:"carousel-example",override:!0}),r=document.getElementById("data-carousel-prev"),a=document.getElementById("data-carousel-next");r.addEventListener("click",(()=>{o.prev()})),a.addEventListener("click",(()=>{o.next()}))},renderMoreProducts=()=>{const e=document.getElementById("more-products");let t=new Set;for(;t.size<4;)t.add(Math.floor(Math.random()*products.length));const n=Array.from(t).map((e=>products[e]));e.innerHTML="",n.map((t=>{const n=document.createElement("div");n.classList.add("group","relative","cursor-pointer"),n.innerHTML=`\n      <div class="w-full aspect-[1/1] overflow-hidden rounded-md group-hover:opacity-75 p-4 bg-gray-50">\n          <img\n              src="${t.image[0]}"\n              alt="${t.name}"\n              class="object-fit w-full aspect-[1/1] object-contain object-center"\n          />\n      </div>\n      <h3 class="mt-4 text-sm text-gray-700">\n          <a href="./product.html">\n              <span class="absolute inset-0"></span>\n              ${t.name}\n          </a>\n      </h3>\n      <p class="mt-1 text-sm text-gray-500">${t.label}</p>\n      <p class="mt-1 text-sm font-medium text-gray-900">$${t.price}</p>\n    `,e.appendChild(n)}))};document.addEventListener("DOMContentLoaded",(()=>{renderCarousel(),renderMoreProducts()}));
